package com.dest.pattern.day2;

/**
 * 
 */
public class pattern12 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=5;i++) {
			for(int j = 1;j<=5;j++) {
				System.out.print("_");
			}
			for(int j = 1;j<=5;j++) {
				System.out.print("*");
			}
			System.out.println();
		}

	}

}
